function csl_rpt( p, t, s, ttle )%CSL_RPT - Produces the final figure and results for the Papadopulos Cooper model%                % Syntax: csl_rpt( p, t, s, ttle )%%   p = parameters of the Papadopulos-Cooper model%   t = measured time%   s = measured drawdown%   ttle = Title of the figure %% Description:%   Produces the final figure and results for the Papadopulos Cooper %   model (1967).%% WARNING: to use CSL_RPT, one needs first to call CS_PRE to define the %          variables that are required for the interpretation.%%    Reference: Cooper, H.H.J., J.D. Bredehoeft, and I.S. Papadopulos.%    1967. Response of a finite-diameter well to an instantaneous charge of%    water. Water Resources Research 3, no. 1: 263-269. %% See also: csl_dmo, csl_dim, csl_pre, csl_gss%if( size(t,1)==1 )    t=t';endif( size(s,1)==1 )    s=s';endglobal CSL_RW CSL_RCif(  isempty(CSL_RW) )     disp(' ERROR: csl_rpt: You must run CSL_PRE before using the csl model')    return;endif(nargin<4) % Default value for d if not given by user  ttle='Slug test';end% Rename the parameters for a more intuitive check of the formulasrw=CSL_RW; rc=CSL_RC;cd=p(1); t0=p(2); % Compute the transmissivity and storativityT=0.5*rc^2/t0;S=0.5./cd*(rc/rw)^2;% Compute the radius of investigation according to Guyonnet et al. (1993)n=0.462;m=0.495;xlim=2.5;tdl=cd.*t(end)./t0;x=tdl.^n./cd.^m;if x<xlim    rdi=rw.*3.54.*tdl.^n;else    rdi=rw.*8.37.*cd.^m;end% Calls an internal script that computes drawdown, derivative, and residualsfunc='csl'; rpt_cmp% Defines the text of the left side of the legend lgdl=char('Test data:');lgdl=char(lgdl, sprintf(' Well radius rw: %0.2g m',rw));lgdl=char(lgdl, sprintf(' Casing radius rc: %0.2g m',rc));lgdl=char(lgdl, ' ');lgdl=char(lgdl, 'Hydraulic parameters:');lgdl=char(lgdl, sprintf(' Transmissivity T: %3.1e m^2/s',T));%lgdl=char(lgdl, sprintf(' Storativity S: %3.1e',S));% Defines the text of the right side of the legend lgdr=char('Cooper-Bredehoeft-Papadopulos Model');lgdr=char(lgdr, ' ');lgdr=char(lgdr, 'Fitting parameters:');lgdr=char(lgdr, sprintf(' C_D : %0.2g ',cd));lgdr=char(lgdr, sprintf(' t_0 : %0.2g s',t0));lgdr=char(lgdr, sprintf(' mean residual: %0.2g m',mr));lgdr=char(lgdr, sprintf(' 2 standard deviation: %0.2g m',sr));% Calls an internal script that places the legendrpt_lgd_slug% Calls an internal script that plots the model and the datarpt_llt_slug