function grg_rpt( p, t, s, d, ttle )%GRG_RPT - Produces the final figure and results for the Gringarten & Ramey model%                % Syntax: grg_rpt( p, t, s, d, ttle )%%   p(1) = a = slope of the late time straight line%   p(2) = t0%   t = measured time%   s = measured drawdown%   d(1) = Q  = Pumping rate%   d(2) = S  = Storage coefficient%   ttle = Title of the figure %% Description%   Produces the final figure and results for Gringarten model (1974).%% See also: grg_dmo, grg_dim, grg_gss%if(nargin==4) % Default value for d if not given by user  ttle='Diagnostic plot';end% Rename the parameters for a more intuitive check of the formulasQ=d(1);a=p(1); t0=p(2); % Compute the transmissivity, product Sxf^2T=Q/(4*pi*a);Sxf2=t0/T;% Calls an internal script that computes drawdown, derivative, and residualsfunc='grg'; rpt_cmp% Defines the text of the left side of the legend lgdl=char('Test data:');lgdl=char(lgdl, sprintf(' Discharge rate: %3.2e m^3/s',Q));lgdl=char(lgdl, ' ');lgdl=char(lgdl, 'Hydraulic parameters:');lgdl=char(lgdl, sprintf(' Transmissivity T: %3.1e m^2/s',T));lgdl=char(lgdl, sprintf(' S xf^2: %2.1e m^2',Sxf2));% Defines the text of the right side of the legend lgdr=char('Gringarten-Ramey (1975) Model');lgdr=char(lgdr, ' ');lgdr=char(lgdr, 'Fitting parameters:');lgdr=char(lgdr, sprintf(' slope a: %0.2g m',a));lgdr=char(lgdr, sprintf(' intercept t0: %0.2g m',t0));lgdr=char(lgdr, sprintf(' mean residual: %0.2g m',mr));lgdr=char(lgdr, sprintf(' 2 standard deviation: %0.2g m',sr));% Calls an internal script that place the legendrpt_lgd% Calls an internal script that plot the model and the datarpt_plt